INSERT INTO `yazilim` (`id`, `baslik`, `icerik`, `keywords`, `description`, `ust_id`) VALUES
(3, 'NarPOS Restoran Otomasyonu', '<h1>&nbsp;</h1>\r\n\r\n<h2><strong>RESTORAN OTOMASYON PROGRAMI</strong></h2>\r\n\r\n<p><u>Restoran&nbsp;</u><u>otomasyon</u><u>&nbsp;programı</u>&nbsp;restoran, cafe, pastane, bar, kantin, sosyal tesis vb. adisyon sistemi gerekli olan işletmelerin işlerini kolaylaştırmak ve kalitesini artırmak i&ccedil;in geliştirilmiş bir programdır.</p>\r\n\r\n<p>Farklı &ouml;deme şekillerini (alman usul&uuml;, par&ccedil;a par&ccedil;a &ouml;deme vs.) g&ouml;z &ouml;n&uuml;ne alarak hazırlanan NarPOS&nbsp;<u>restoran otomasyon programı&nbsp;</u>olduk&ccedil;a basit ve kolay anlaşılabilir olması y&ouml;n&uuml;nden bilgisayar kullanmayı bilmeyen kullanıcılar tarafından bile kolay ve kısa bir s&uuml;rede &ouml;ğrenilebilir.</p>\r\n\r\n<p>Yenilenen ve geliştirilen NarPOS&nbsp;<u>restoran otomasyon programı&nbsp;</u>sorunsuz bir şekilde &ccedil;alışmaktadır. En yoğun zamanlarınızda bile donma, kapanma vs. gibi sorunlar &ccedil;ıkarmaz. Hi&ccedil;bir gecikme olmadan mutfak fişlerinizi keser, siparişlerinizi eksiksiz tutar. Yani NarPOS&nbsp;<u>restoran otomasyon programı&nbsp;</u>en yoğun zamanlarınızda işinizi d&uuml;zene sokan ve kolaylaştıran yardımcınız olur.</p>\r\n\r\n<p><u>NarPOS Restoran otomasyon programı&nbsp;</u>mekanınıza &ouml;zel olarak tasarlanır, ona uygun el terminalleri, adisyon kesim noktaları hesaplanır. Size uygun ve &ouml;zel bir&nbsp;<u>restoran otomasyon programı&nbsp;</u>projelendirilir, b&ouml;ylelikle oluşabilecek sorunlar en aza indirilir.</p>\r\n\r\n<p><u>NarPOS Restoran otomasyon programı</u>nın bir diğer yararı ise hesap alma sırasında oluşabilecek sorunları engellemesidir. Bunun yanında hem zaman kazanmanızı sağlar, hem s&uuml;re kaybınızı &ouml;nler hem de sorunları daha oluşmadan &ccedil;&ouml;zer.</p>\r\n\r\n<p><u>NarPOS Restoran otomasyon programı</u>&nbsp;ciroların ayrıntılı raporlamasıyla ka&ccedil;ak ve eksiklerin takip edilmesini sağlar. B&ouml;ylece hi&ccedil; yoktan gelebilecek zararların &ouml;n&uuml;ne ge&ccedil;ilir.</p>\r\n\r\n<p><a href=\"http://narpos.com.tr/wp-content/uploads/2017/05/8.png\"><img alt=\"\" src=\"http://narpos.com.tr/wp-content/uploads/2017/05/8-300x118.png\" style=\"height:232px; width:590px\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n', '   NarPOS Restoran Otomasyonu', 'NarPOS Restoran Otomasyonu', 6),
(5, 'Restaurant Nedir?', '<h1>&nbsp;</h1>\r\n\r\n<h2><strong>Restaurant Nedir?</strong></h2>\r\n\r\n<p><strong><u>Restaurant</u></strong>;&nbsp;<u>restoran</u>,&nbsp;<u>lokanta</u>. Belirli bir &uuml;cret karşılığında hizmet (yiyecek, i&ccedil;ecek, sunum vs.) verilen yerdir. Yemeklerin hazırlanacağı mutfak ve yiyeceklerin servis edileceği salon veya salonlardan oluşur.</p>\r\n\r\n<p><u>Restaurant</u>lar g&uuml;nl&uuml;k olarak kullandığımız yemek yeme ve sosyalleşme alanlarıdır. Eski zamanlardan bu yana insanın olduğu her yerde bulunan&nbsp;<u>restaurant</u>lara her &ccedil;ağda ve her yerde farklı isimler verilse de temel olarak aynı ama&ccedil;lara hizmet etmektedir. Bulunduğumuz &ccedil;ağda insanların sıklıkla kullandığı&nbsp;<u>restaurant</u>ların tercih edilme sebebi; vakit kaybetmeden, uğraşmadan besleyici ve lezzetli yiyeceklere ulaşmaktır. Fakat&nbsp;<u>restaurant</u>ların insanlara kazandırdığı g&ouml;z ardı edilmeyecek bir &ouml;zelliği vardır; sosyalleşme ortamı sağlamak.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Teknoloji ilerledik&ccedil;e&nbsp;<u>restaurant</u>ların hizmetlerinde &ccedil;eşitli gelişmeler ve &ccedil;eşitlilik g&ouml;zlenmiştir. Bunlardan biri insanların evden &ccedil;ıkmadan yiyeceklere, i&ccedil;eceklere ulaşmasını sağlayan eve servis hizmetidir. Bir&ccedil;ok farklı teknolojik gelişmelerin olmasıyla birlikte bunun bir d&uuml;zene ve sisteme konulması gerekliliği ortaya &ccedil;ıkmıştır. B&ouml;ylelikle&nbsp;<strong><u>restaurant sistemi</u></strong>&nbsp;geliştirilmeye başlanmıştır. G&uuml;n&uuml;m&uuml;zde bir&ccedil;ok&nbsp;<u>restaurant</u>,&nbsp;<u>restaurant sistemleri</u>ni kullanarak işlerini daha d&uuml;zenli ve g&uuml;venli bir şekilde yapmaktadırlar.&nbsp;Y&uuml;ksek yazılım m&uuml;hendislerinin kullanıcı ve t&uuml;ketici bazlı ortaya &ccedil;ıkarttığı NarPOS Otomasyon Yazılımı&rsquo; da bu gerekliliklerin hepsini ve daha fazlasını b&uuml;nyesinde bulundurarak sizin ve işletmenizin verimliliğini &uuml;st sıralara &ccedil;ıkartıyor.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"http://narpos.com.tr/wp-content/uploads/2017/05/2.png\"><img alt=\"Restaurant Nedir?\" src=\"http://narpos.com.tr/wp-content/uploads/2017/05/2-300x169.png\" style=\"height:292px; width:518px\" /></a></p>\r\n', '  Restaurant Nedir?', 'Restaurant; restoran, lokanta. Belirli bir ücret karşılığında hizmet (yiyecek, içecek, sunum vs.) verilen yerdir. Yemeklerin hazırlanacağı mutfak ve yiyeceklerin servis edileceği salon veya salonlardan oluşur.', 9),
